% №1
%-функция преобразования текстового сообщения в цифровой поток данных (последовательность бит) п
function [BitSeq] = txt2Bit(TxtSeq)  
    BitSeq = reshape(dec2bin(double(TxtSeq),8),1,[]); 
end

% №2
%функция вычисления вероятности ошибки
function BER = berCalc(TransmitSeq, ReceiveSeq)
    count = 0;
    for i = 1:length(TransmitSeq)
        if(TransmitSeq(1,i) ~= ReceiveSeq(1,i))
            count = count + 1;
        end
    end
    BER = count / length(TransmitSeq);
end

% №3
% функция по внесению случайных ошибок в текстовое сообщение
% где ErrorMessage – сообщение с ошибками, MessageTxt – сообщение без ошибок, 
% N – число случайных ошибок
function ErrorMessage = SetError (MessageTxt, N)
    messageBitSeq = txt2Bit(MessageTxt);
    errorBitsIndexes = randi([1 length(messageBitSeq)], 1, N);
    for i = 1:length(errorBitsIndexes)
        errorBit = messageBitSeq(1, errorBitsIndexes(1, i));
        % если в исходном сообщении '1' - меняем на '0'
        if (errorBit == '1') 
            messageBitSeq(1, errorBitsIndexes(1, i)) = dec2bin(double(0),1);
        else
            messageBitSeq(1, errorBitsIndexes(1, i)) = dec2bin(double(1),1);
        end     
    end        
    ErrorMessage = bit2Txt(messageBitSeq);
end

function Message = bit2Txt(BitSeq)
    Message = reshape(char(bin2dec(reshape(BitSeq,[],8))),1,[]);   
end

function [Signal] = bit2signal(BitSeq)
    Signal = zeros(0);    
    for i = 1:length(BitSeq)        
        if (BitSeq(1, i) == dec2bin(double(0),1))            
            Signal(end+1) = double(0.5);
        else
            Signal(end+1) = double(3);            
        end     
    end
end

function ReceiveSeq = channel(TransmitSeq, Ps, Pn, a, L)
    Signal = bit2signal(TransmitSeq);
    TransmitSignal = Signal;
    
    n = length(TransmitSignal);
    A = 1 - a * L * 0.001;
    
    SNR = double(Ps / Pn)
    global V;
    global numSteps;
    global mc;
    t = length(TransmitSeq) / V;
    for i = 1 : t
        if (mod(i, (t / numSteps)) == 0)
            Pn = nextState(Pn, mc);
            SNR = double(Ps / Pn);
        end        
        TransmitSignal = awgn(TransmitSignal, SNR, Ps);
    end   
    ReceiveSignal = TransmitSignal * A;    
    
    t = (0:0.001:80-0.001)';
    clf
    plot(t,Signal);
    hold on    
    plot(t,ReceiveSignal);
    % Для нагладного отображения результатов
    % на график выводим только сигнал в интервале с 10 сек до 10.1 сек
    xlim([10 10.1]); 
    legend('Original Signal','Signal with AWGN');
    
    ReceiveSeq = signal2bit(ReceiveSignal);
end

% Функция рассчитывает мощность передаваемого сигнала
function Ps = calcPs(Signal)    
    Ps = sum(Signal.^2)/length(Signal);
end

%-функция преобразования текстового сообщения 
% в цифровой поток данных (последовательность бит)
function [BitSeq] = signal2bit(Signal)  
    BitSeq = '';    
    for i = 1:length(Signal)        
        if (Signal(1, i) >= double(1.75))            
            BitSeq = append(BitSeq, num2str(1));
        else
            BitSeq = append(BitSeq, num2str(0));            
        end     
    end  
end

%Случайное изменение качества канала связи за время передачи 
%одного отсчета   дискретного сигнала
%на основе матрицы вероятностей переходов
function State = nextState(PreviousState, mc)
    PreviousStateString = num2str(PreviousState);
    for i = 1:length(mc.StateNames)
        if (PreviousStateString == mc.StateNames(i))
            break;
        end
    end
    x0 = zeros([1 9]);
    x0(i) = 1;
    X1 = simulate(mc,1,'X0',x0);
    State = str2double(mc.StateNames(X1(2)));
end
